let obj = fetchSync.json(`https://www.reddit.com/r/FloridaMan/random/.json`)
obj = Array.isArray(obj) ? obj[0] : obj
if (obj.error || !obj.data) {
  throw `shreddy is dead ???`
} else if (obj.data.children.length) {
  const post = _.shuffle(obj.data.children)[0].data
  let hl = post.title.replace(/florida man|florida woman|floridaman|floridawoman|florida/gi, 'Shreddy')
  print(`${hl.replace(/\w+/g, IRC.breakHighlight)} (${post.url})`)
}
