const text = IRC.log.random(1+(new Date%2)).map(d => d.message).join` `.split` `;

const out = fetchSync.json('https://api.openai.com/v1/completions', {
    method: 'POST',
    headers: {
        "Content-Type": "application/json",
        "Authorization": `Bearer ${IRC.secret}`,
    },
    body: JSON.stringify({
        model: 'text-davinci-002',
        prompt: input || text,
        temperature: 0.7,
        max_tokens: 256,
        top_p: 1,
        frequency_penalty: 0,
        presence_penalty: 0
    }),
});

print(out.choices.map(d=>d.text).join` `.replace(/\s+/g, ' ').trim().slice(0,400))
