let aryIannaTimeZones = Intl.supportedValuesOf('timeZone');
let time_format_options = {
  timeZoneName: "short",
  year: "numeric",
  month: "2-digit",
  day: "2-digit",
  hour: "2-digit",
  hour12: false,
  minute: "2-digit",
  second: "2-digit",
};

const capitalize = (s) => {
  if (typeof s !== 'string') return ''
  return (s.charAt(0).toUpperCase() + s.slice(1)).replace(" ","_")
}

let date = new Date();
let tz = aryIannaTimeZones.filter( x => x.includes(capitalize(input)) )[0]

if(!tz){
  print("invalid timezone")
}else{
  time_format_options.timeZone = tz;
  print(
    print(date.toLocaleString("ja-JP", time_format_options).replaceAll('/','-'))
  )
}
