command:
const capitalize = (s) => {
    if(s) return s.charAt(0).toUpperCase() + s.slice(1)
}
const obj = fetchSync(`https://www.worldcubeassociation.org/api/v0/records`, {type: 'json'});
const CONTINENTS = Object.keys(obj.continental_records).map((s) => s.replace("_",""))
const COUNTRIES = Object.keys(obj.national_records)
const event = IRC.command.params[1]
const geo = capitalize(IRC.command.params[0])

function pad(num){
    if(num < 10){
        return "0" + num.toFixed(2)
    }else{
        return num.toFixed(2)
    }
}

function format_number(num){
    if(num > 59.99){
        return Math.floor(num / 60) + ":" + pad(num % 60)
    }else{
        return num
    }
}

function print_event(event, segment){
    data = segment[event]
    const devider = event == "333fm" ? 1 : 100;
        if (data) {
            print(`{y}[${event}]{/} Single: {g}${format_number((data.single/devider).toFixed(2))}{/} Average: {g}${format_number((data.average/100).toFixed(2))}`);
        }
        else {
            print('No Record found for ' + event + ' for this country. Valid events:');
            print(Object.keys(segment).join`, `)
        }
}

if(geo && event){
    if(geo === "World"){
        print_event(event, obj.world_records)
    }
    else if(CONTINENTS.indexOf(geo) !== -1 ){
        print_event(event, obj.continental_records["_"+geo]) 
    }
    else if(COUNTRIES.indexOf(geo) !== -1){
        print_event(event, obj.national_records[geo])
    }
    else{
        print("Geo not found")
        print("Acceptable Continents are")
        print(CONTINENTS.join`, `)
        print("Acceptable Countries are")
        print(COUNTRIES.join`, `)
    }
}else{
    print("Usage wca.records(geo, event)")
    print("Valid goes are world, a continent or a country")
}
