function get(){
  try{
    const API_URL_FORECAST = "http://api.openweathermap.org/data/2.5/onecall?exclude=current,minutely,hourly,alerts"
    const API_URL_LAT_LONG = "http://api.openweathermap.org/data/2.5/weather"
    let args = input.split(",")
	const user = IRC.message.from
    if (input == ''){
      if(hasSetDefault(user)){
      	args = [getDefault(user)]
      }
      else{
        print(`usage: {p}${IRC.trigger}weather{/} [location] [,state] [,country] {dgr}(service provided by openweathermap.org){/}`)
        return
      }
    }
    // We need to do 2 requests as the forecasting API only takes lat / long (urgh)
    const request_lat_long = fetchSync.json(API_URL_LAT_LONG + `?q=${args.join(",")}&appid=${IRC.secret}&units=metric`);
    const lat = request_lat_long.coord.lat
    const lon = request_lat_long.coord.lon
    const request = fetchSync.json(API_URL_FORECAST + `&lat=${lat}&lon=${lon}&appid=${IRC.secret}&units=metric`);
    print(`3 Day Forecast for ${request_lat_long.name}, ${request_lat_long.sys.country} Timezone: {dg}${request.timezone}{/} `)
    for(var i = 0; i < 3; i++){
      format_weather(request.daily[i])
    }
  }
  catch(error){
    print(`No Results for Location ${input}`)
  }
}

if(!module.required){
    get();
